package lecture_4;

public class data_type_demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b = (byte)(300);
		short s = 68;
		int i = 789;
		long l = 5678856478l;     //literal hardcore is in integer so no allow to write more than 9 digit
		System.out.println(b);
		System.out.println(s);
		System.out.println(i);
		System.out.println(l);
		
		int a = 023;    // if number start with 0 than it is octal  number
		System.out.println(a);

	}

}
