package string;

public class String_demo_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Hello" + 10 + 20 + "bye"); // string + string +  string + string
		System.out.println("Hello" + (10 + 20) + "bye"); // string + string(int + int) + string
		System.out.println(10 + 20 + "Hello" + "bye");  // int+ int+ string + string 
		

	}

}
